--修改recipe錯誤的Lv2


--每個增加+13
--定義迴圈參數
DECLARE  
@TotalNum INT, --執行次數(目前跑到的食譜PK)
@Num INT ,      --應該跑到的食譜PK
@diff INT


--設定迴圈參數
SET @TotalNum = 14--執行次數(目前跑到的食譜PK)
SET @Num =1        --應該跑到的食譜PK
SET @diff =13		--差額

--執行WHILE迴圈
WHILE @Num <= @TotalNum  --當目前次數小於等於執行次數
BEGIN

	Update [iSpanDataBaseICook].[dbo].[RECIPE_食譜]
	Set [FEATURED_CATEGORY精選分類LEVEL_TWO] = CONVERT(INT,[FEATURED_CATEGORY精選分類LEVEL_TWO]+@diff)
	Where [FEATURED_CATEGORY精選分類LEVEL_ONE] = @Num

    --設定目前次數+1
    SET @Num = @Num + 1
END

--改尷尬的清明節
