SET @diff2 =13

	Update [iSpanDataBaseICook].[dbo].[RECIPE_食譜]
	Set [FEATURED_CATEGORY精選分類LEVEL_TWO] = CONVERT(INT,[FEATURED_CATEGORY精選分類LEVEL_TWO]+@diff2)
	Where [RECIPE食譜_PK] in (
12346356,
